import React, { useEffect, useRef } from 'react';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import './About.css';

const About = () => {
  const fishRef = useRef([]);
  const valuesRef = useRef(null);
  const imageRef = useRef(null);

  useEffect(() => {
    // Page load animation
    const tl = gsap.timeline();

    tl.fromTo('.about-hero h1',
      { y: 100, opacity: 0 },
      { y: 0, opacity: 1, duration: 1, ease: 'back.out(1.7)' }
    )
    .fromTo('.about-content',
      { x: -100, opacity: 0 },
      { x: 0, opacity: 1, duration: 0.8, stagger: 0.2 },
      '-=0.5'
    )
    .fromTo('.about-image',
      { x: 100, opacity: 0 },
      { x: 0, opacity: 1, duration: 0.8 },
      '-=0.6'
    );

    // Swimming fish animation
    fishRef.current.forEach((fish, index) => {
      if (fish) {
        gsap.to(fish, {
          x: window.innerWidth + 100,
          duration: 15 + index * 2,
          repeat: -1,
          ease: 'none',
          delay: index * 3
        });

        gsap.to(fish, {
          y: `+=${Math.random() * 50 - 25}`,
          duration: 4 + index,
          repeat: -1,
          yoyo: true,
          ease: 'sine.inOut'
        });
      }
    });

    // Values animation on scroll
    gsap.fromTo('.value-bubble',
      { scale: 0, opacity: 0, rotation: -180 },
      {
        scale: 1,
        opacity: 1,
        rotation: 0,
        duration: 0.8,
        stagger: 0.2,
        ease: 'back.out(1.7)',
        scrollTrigger: {
          trigger: valuesRef.current,
          start: 'top 80%',
          end: 'bottom 20%',
          toggleActions: 'play none none reverse'
        }
      }
    );

    // Floating bubbles around text
    const createFloatingBubbles = () => {
      for (let i = 0; i < 5; i++) {
        const bubble = document.createElement('div');
        bubble.className = 'floating-bubble';
        bubble.style.left = `${Math.random() * 100}%`;
        bubble.style.top = `${Math.random() * 100}%`;
